window.addEventListener("DOMContentLoaded",function(){var form=document.getElementById("contact-form");var button=document.getElementById("contact-form-button");var status=document.getElementById("contact-form-status");let parameters=new URLSearchParams(window.location.search);function success(){form.reset();button.style="display: none ";status.innerHTML="Thanks! Contact form is submitted successfully.";var utm=parameters.get("utm_source");var med=parameters.get("utm_medium");if(parameters.has("gclid")){var clickid=parameters.get("gclid");}
else if(parameters.has("li_fat_id")){var clickid=parameters.get("li_fat_id");}
else if(parameters.has("fbclid")){var clickid=parameters.get("fbclid");}
else{clickid="null"}
document.getElementById('utmSrc').value=utm;document.getElementById('utmCamp').value=med;document.getElementById('clickId').value=clickid;}
function error(){status.innerHTML="Oops! There was a problem.";}
form.addEventListener("submit",function(ev){ev.preventDefault();var data=new FormData(form);ajax(form.method,form.action,data,success,error);});});function ajax(method,url,data,success,error){var xhr=new XMLHttpRequest();xhr.open(method,url);xhr.setRequestHeader("Accept","application/json");xhr.onreadystatechange=function(){if(xhr.readyState!==XMLHttpRequest.DONE)return;if(xhr.status===200){success(xhr.response,xhr.responseType);}else{error(xhr.status,xhr.response,xhr.responseType);}};xhr.send(data);}